mapper.sql:
FROM user a
WHERE 1=1
AND a.uUseNy = #{shUseNy}
AND a.uDelNy = #{shDelNy}


    AND a.useq = #{shValue}
    AND a.uName LIKE CONCAT('%',#{shValue},'%')
    AND a.uItem LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into user ( useq, uName, uPhoneNum, uGender, uIdEmail, uBirth, uItem, uPassword, uUseNy, uDelNy
) values ( #{useq}, #{uName}, #{uPhoneNum}, #{uGender}, #{uIdEmail}, #{uBirth}, #{uItem}, #{uPassword}, #{uUseNy}, 0 )
SELECT last_insert_id()

mapper.update:
update user set
uDelNy=1
where useq=#{useq}
